import React, {useState, useEffect} from 'react'
import Card from '../UI/Card'
import './style.css'
import blogPost from '../../data/blogdata.json'


const Blogpost = (props) => {

  const [post, setPost]=useState({
    id:"",
    blogTitle:"",
    blogCategory:"",
    blogImage:""
  });
  const [postId, setPostId]=useState('')


  useEffect(()=>{
    const postId= props.match.params.postId;
    const post= blogPost.data.find(post => post.id == postId);
    setPost(post);
    setPostId(postId);
  }, [post, props.match.params.postId]);

  if(post.blogImage=="") return null


  return(
    <div className="blogPostContainer">
      <Card >
        <div className="blogHeader">
          <span className="blogCategory">{post.blogCategory}</span>
          <h1 className="postTitle">{post.blogTitle}</h1>
          <span className="postBy">Posted on April 1, 2020 by Ananya Triapthi</span>
        </div>

        <div className="postImageContainer">
          <img alt="Post Image" src={require('../../assets/images/'+post.blogImage)}/>
        </div>

        <div className="postContent">
          <h3>Post title</h3>
          <p>Harry Edward Styles (born 1 February 1994) is an English singer, songwriter and actor. Born in Redditch, Worcestershire, his musical career began in 2010 as a solo contestant on the British music competition series The X Factor. Following his elimination early on, he was brought back to join the boy band One Direction, which went on to become one of the best-selling boy bands of all time.

After the band's indefinite hiatus in 2016, Styles released his self-titled debut album through Columbia Records the following year. It was certified platinum in the US, while its lead single, "Sign of the Times", topped the UK Singles Chart. He made his acting debut in Christopher Nolan's 2017 war film Dunkirk. Styles' second album, Fine Line (2019) debuted atop the US Billboard 200 and had the biggest sales debut from an English male artist in the US. It also yielded the single "Watermelon Sugar", which topped the US Billboard Hot 100.

Throughout his career, Styles has been nominated for three Brit Awards, winning one, and received an ARIA Music Award. Other than his music, Styles is also known for his flamboyant fashion.</p>
        </div>
      </Card>

    </div>
   )
 }

export default Blogpost
