import React, {useState, useEffect} from 'react'
import Card from '../UI/Card'
import './style.css'
import bloginfo from '../../data/blogdata.json'


const Blogpost = (props) => {

  const [post, setPost]=useState({});


  useEffect(()=>{
    const postId= props.match.params.post;
    const post= bloginfo.data.find(post => post.id == postId);
    setPost(post);
  })


  return(
    <div className="blogPostContainer">
      <Card>
        <div className="blogHeader">
          <span className="blogCategory">Featured</span>
          <h1 className="postTitle"></h1>
          <span className="postBy">Posted on April 1, 2020 by Ananya Triapthi</span>
        </div>
        <div className="postImageContainer">
          <img alt="Post Image" src={require('../../assets/images/anna.jpg')}/>
        </div>
        <div className="postContent">
          <h3>Post title</h3>
          <p>This is a beautiful post</p>
        </div>
      </Card>

    </div>
   )
 }

export default Blogpost
