import React, {useState, useEffect} from 'react'
import Card from '../UI/Card'
import './style.css'
import blogPost from '../../data/blogdata.json'
import { NavLink } from 'react-router-dom'

const Sidebar = (props) => {

  const [posts, setPosts]=useState([]);


  useEffect(()=>{
    const posts= blogPost.data
    setPosts(posts);
  }, [posts]);


  return(
    <div className="sidebarContainer" style={{
      width:props.width
    }}>
      <Card style={{marginBottom:"20px", padding:'15px', boxSizing: 'border-box'}}>
        <div className="cardHeader">
          <span>About Us</span>
        </div>
        <div className="HarryContainer">
          <img alt="Harry" src="https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/harry-styles-arriving-at-the-dunkirk-world-premiere-at-news-photo-1574431134.jpg?crop=1.00xw:0.729xh;0,0.0173xh&resize=980:*"/>
        </div>
        <div className="cardbody">
          <p className="personalBio">Hey, I am Ananya Tripathi. This is my first website. Hope you like it!! :) </p>
        </div>
      </Card>

      <Card style={{marginBottom:"20px", padding:'15px', boxSizing: 'border-box'}}>
        <div className="cardHeader1">
          <span>Social Media</span>
          <div className="headersocial">
            <a className="socialLink" href="https://www.instagram.com/"><img className="3dicon" src={require('../../assets/icons/insta3d.png')}></img>  </a> 
            <a className="socialLink" href="https://www.facebook.com/"><img className="3dicon" src={require('../../assets/icons/facebook3d.png')}></img>  </a>
            <a className="socialLink" href="https://www.linkedin.com/in/ananya-tripathi-477178194/"><img className="insta" src={require('../../assets/icons/linkedin3d.png')}></img> </a>
            <a className="socialLink" href="https://in.pinterest.com/"><img className="insta" src={require('../../assets/icons/pin3d.png')}></img> </a>
        </div>
        </div>
      </Card>
      
      <Card style={{marginBottom:"20px", padding:'15px', boxSizing: 'border-box'}}>
        <div className="cardHeader">
          <span>Recent Posts</span>
        </div>

        <div className="posts">

          {
            posts.map(post => {
              return(
                <NavLink key={post.id} to={`/post/${post.id}`}>
                  <div className='post'>
                  <h3>{post.blogTitle}</h3>
                  <span>April 1, 2020</span>
                </div>
                </NavLink>
                
              );
            })
          }

        </div>
      </Card>

    </div>
    
   )
 }

export default Sidebar
